
public class Stars {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int row, star;
		   
		    for (row = 1; row <= 4; row++) {
		      for (star = 1; star <= row; star++) {
		        System.out.print("*");
		      }
		      System.out.println(); 
		    }
	}

}
